  <!-- Header-->
  <header *ngIf="isBrowser() && hasCart();" class="bg-dark py-6">
    <div class="cover" style="background-image: url(https://firebasestorage.googleapis.com/v0/b/clothingapp-ed125.appspot.com/o/Resources%2FSample-Walls%2FHome%2F1.jpg?alt=media);">
        <div class="d-flex align-items-center justify-content-center" style="height: 100px; font-weight: bold;font-size:35px">
            <p class = "text-center" style = "line-height:1.2em;text-shadow: 2px 2px #42424281;">
              ORDER HAS BEEN PLACED
             </p>
           </div>
      </div>
  </header>

  <section *ngIf="isBrowser() && hasCart();" [hidden]="stillLoadingProducts()" class="py-5">
    <div class="container pb-5 mb-2 mb-md-4">
        <div class="row">
          <!-- List of items-->
          <!-- <div class="d-flex justify-content-left align-items-center pb-4 pb-sm-5 mt-1 ms-2 ps-1">
            <a class="btn btn-outline-primary btn-sm ps-2" href="shop-grid-ls.html"><i class="ci-arrow-left me-2"></i>Continue shopping</a>
          </div> -->

          <section class="col-lg-8">

            <div class="bg-white rounded-3 shadow-lg p-2 pb-1">
                <div class="py-0 px-xl-2">
                  <div [hidden]="stillLoadingProducts()" class="text-accent">
                    <h2 class="h6 mb-3 pb-1 ps-2 mt-2">Shipping Address</h2>
                    <h3 class="fw-normal ps-2" style="font-style: italic">{{getShippingAddressFirst()}}</h3>
                    <h3 class="fw-normal ps-2" style="font-style: italic">{{getShippingAddressSecond()}}</h3>
                    <h3 class="fw-normal ps-2" style="font-style: italic">{{getShippingAddressThird()}}</h3>
                    <!-- <p><a href="/{{storeInfo().username}}/shipping-address" class="text-primary text-decoration-none ps-4" style="font-weight: bold;">Change</a></p> -->
                  </div>
                </div>
            </div>
<!-- 
            <div class="bg-white rounded-3 shadow-lg p-2 pt-3 pb-1 mt-3">
                <div class="py-0 px-xl-2">
                  <div [hidden]="stillLoadingProducts()" class="text-accent">
                    <h2 class="h6 mb-3 pb-1 ps-4 mt-2">2. Payment Method </h2>
                    <h3 class="fw-normal ps-4" style="font-style: italic">{{getBillingAddressFirst()}}</h3>
                    <p><a href="/{{storeInfo().username}}/billing-info" class="text-primary text-decoration-none ps-4" style="font-weight: bold;">Change</a></p>
                    </div>
                </div>
            </div> -->
            <!-- Item-->
            <div class="bg-white rounded-3 shadow-lg p-4 pt-3 mt-3">
                <h2 class="h6 mb-1 pb-1 ps-1 mt-2">Order #: {{selectedOrder()!.orderID}}</h2>
                <div class="py-2 px-xl-2">
                    <div *ngFor="let product of selectedOrder()!.products" class="d-sm-flex justify-content-between align-items-center pb-3 pt-2">
                        <div class="d-block d-sm-flex align-items-center text-center text-sm-start"><a class="d-inline-block flex-shrink-0 mx-auto me-sm-4" href="/{{storeInfo().username}}/products/{{product.product!.productID}}"><img style="border-radius: 2.5%;" src="{{product.product!.url}}" onerror="this.src='https://firebasestorage.googleapis.com/v0/b/clothingapp-ed125.appspot.com/o/Resources%2Flink_empty.jpeg?alt=media';" width="160" alt="Product"/></a>
                          <div class="pt-2">
                              <p><a href="/{{storeInfo().username}}/products/{{product.product!.productID}}" class="text-dark text-decoration-none" style="font-weight: bold;">{{product.product?.name ?? ""}}</a></p>
                            <div class="fs-sm"><span class="text-muted me-2">Size:</span>{{product.size ?? "M"}}</div>
                            <div class="fs-sm"><span class="text-muted me-2">Color:</span>{{getColor(product.product)}}</div>
                            <div class="fs-lg text-accent pt-2">{{formatPrice(product.product!.price / 100)}}</div>
                          </div>
                        </div>
                        <div class="pt-2 pt-sm-0 ps-sm-3 mx-auto mx-sm-0 text-center text-sm-start" style="max-width: 5.5rem;">
                          <label class="form-label" for="quantity1">Quantity</label>
                          <div>
                            <label class = "text-center" style="font-weight: bold; font-size: 22px; width: 100%;" for="quantity1">{{product.quantity ?? 1}}</label>
                          </div>
                        </div>
                      </div>
                </div>
            </div>

          </section>
          <!-- Sidebar-->
          <aside class="col-lg-4 pt-4 pt-lg-0 ps-xl-5">
            <div [hidden]="stillLoading()" class="bg-white rounded-3 shadow-lg p-4 pb-0">
              <div class="py-2 px-xl-2">
                <!-- (click)="placeOrder()" -->
                <a *ngIf="selectedOrder()?.trackingNumber ?? false" class="btn btn-primary btn-shadow d-block w-100 mb-4" type="button" (click)="trackingLink()"><i class="ci-card fs-lg me-2"></i>Track Order</a>

                <div class="text-center mt-4 pt-3 border-top border-bottom">
                <div class="d-flex justify-content-between">
                    <h3 class="fw-bold" style="font-size: 9;">Items ({{itemCount()}}):</h3>
                    <h3 class="fw-normal" style="font-size: 9;">{{formatPrice(total())}}</h3>
                </div>
                <div class="d-flex justify-content-between">
                    <h3 class="fw-bold" style="font-size: 9;">Shipping & Handling:</h3>
                    <h3 class="fw-normal" style="font-size: 9;">{{shipping()}}</h3>
                </div>
                <div class="d-flex justify-content-between">
                    <h3 class="fw-bold" style="font-size: 9;">Sales Tax:</h3>
                    <h3 class="fw-normal" style="font-size: 9;">{{productTax()}}</h3>
                </div>
                </div>
                <div class="d-flex justify-content-between pt-2 mt-2">
                    <h3 class="fw-bold" style="font-size: 9;">Order Total:</h3>
                    <h3 class="fw-normal" style="font-size: 9;">{{orderTotal()}}</h3>
                </div>
              </div>
            </div>
          </aside>
        </div>
      </div>
  </section>
