import { Component, OnInit } from '@angular/core';
import { DomSanitizer } from '@angular/platform-browser';
import { Template } from '../models/template.model';



@Component({
  selector: 'app-design',
  templateUrl: './design.component.html',
  styleUrls: ['./design.component.css']
})
export class DesignComponent implements OnInit {

  myInnerHeight = 0
  templates?: Array<Template>
  sanitizer?: DomSanitizer

  constructor(
    sanitizer: DomSanitizer,
  ) { 
    this.sanitizer = sanitizer
  }

  ngOnInit(): void {
    let height = window.innerHeight
    let width = window.innerWidth

    if (width < height){
      this.myInnerHeight = width * 0.95
    }
    else{
      this.myInnerHeight = height * 0.6
    }

    this.loadTemplate()
  }

  async loadTemplate() {

    // var unsafeImageUrl = this.sanitizer.

    // this.base64Data = this.sanitizer.bypassSecurityTrustUrl(unsafeImageUrl);
  }

}
<div class="imagecontainer"> 
    <div class="container">
        <div class="child" *ngFor="let color of templates![0].colors">
            <div>
                <!-- Product image-->
                <a role="button"><img class="card-img-top" [style.height.px]="myInnerHeight" [style.width.px]="auto" [src]='sanitizer!.bypassSecurityTrustResourceUrl("data:image/jpeg;base64," + color.img)' alt='ok' onerror="this.src='https://firebasestorage.googleapis.com/v0/b/clothingapp-ed125.appspot.com/o/Resources%2Flink_empty.jpeg?alt=media';"/></a>
                <!-- Product details-->


            </div>
        </div>
    </div>
</div>
