import { Component, OnInit, Inject, PLATFORM_ID, ChangeDetectorRef } from '@angular/core';
import { Globals } from '../globals';
import { ActivatedRoute, Router } from '@angular/router';
import { Title, Meta } from '@angular/platform-browser';
import { LoadService, Dict } from '../services/load.service';
import { isPlatformBrowser } from '@angular/common';
import { Product } from '../models/product.model';
import { Country } from '../models/shipping-country.model';
import { ProductInCart } from '../models/product-in-cart.model';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent implements OnInit {

  storeInfo(){return Globals.storeInfo}

  availableCurrencies(){return Globals.availableCurrencies}

  selectedCurrency(){return Globals.selectedCurrency}

  templates(){return Globals.availableTemplates}

  availableTemplates(){return Globals.availableTemplates}

  products(){return Globals.products}

  selectedProduct(){return Globals.selectedProduct}

  selectedTemplate(){return Globals.selectedTemplate}

  selectedCountry!: string
  selectedCode!: string
  loadedCart = false


  cartLength(){
    if (Globals.cart?.length == 0){

      return ""
    }
    return Globals.cart?.length
  }

  cart(){
    return Globals.cart
  }


  getColor(product?: Product){

    let colors = Globals.templates.find(template => template.productCode == product?.productType)?.colors
    return colors?.find(color => color.code == product?.templateColor)?.display ?? "White"
  }

  stillLoadingProducts(){
    return Globals.cart?.filter(product => product.product?.price == undefined)?.length != 0
  }

  calculateShipping($event: any){

    let country = $event.target.options[$event.target.options.selectedIndex].text;

    this.selectedCountry = country
  }

  onKey(event: any) {
    const inputValue = event.target.value;
    this.selectedCode = inputValue
  }

  calculateDiscount(){

    console.log(this.selectedCode)

    // this.selectedCountry = country
  }

  updateProductQty(product: ProductInCart, newQty: string){

    product.quantity = Number(newQty)

    let mappedData: Array<Dict<any>> = []
    let dataToRemove: Array<ProductInCart> = []

    Globals.cart?.forEach((cartProduct, index) => {
      if ((cartProduct.quantity ?? 0) > 0){
        let data = {
          "UID" : cartProduct.product?.uid,
          "Size" : cartProduct.size,
          "Qty" : cartProduct.quantity,
          "Timestamp" : cartProduct.timestamp,
          "Post_ID" : cartProduct.product?.productID
        }
        mappedData.push(data)
      }
      else{
        dataToRemove.push(cartProduct)
      }
    })

    Globals.cart = Globals.cart?.filter(cartProduct => !(dataToRemove.includes(cartProduct)))

    this.loadService.changeCart(mappedData)
    this.cdr.detectChanges()
    // this.selectedCountry = country
  }

  calculate(){
    this.cdr.detectChanges()
  }

  shipping(){
    let shipping = (Globals.availableCurrencies.find(country => country.name_full == this.selectedCountry)?.shipping_rate_usd ?? 0) / 100
    if (shipping > 0){
      return this.formatPrice(shipping)
    }
    return "FREE"
  }

  total(){
    var total = 0

    Globals.cart?.forEach(product => {
      total += (product.product?.price ?? 0) * (product.quantity ?? 1)
    })
    return total / 100
  }

  format(d: Date) {
    return Globals.months[d.getMonth()].substring(0,3) + " " + d.getDate() + " " + d.getFullYear()
  }

  async callback(){
    if (Globals.storeInfo.uid){
      this.addTags(Globals.storeInfo.fullName ?? "Thred", (Globals.storeInfo.profileLink ?? new URL("https://shopmythred.com")).toString(), Globals.storeInfo.bio ?? "Check out my Thred Store!", "shopmythred.com/" + Globals.storeInfo.username)
      if (Globals.availableCurrencies.length == 0 && isPlatformBrowser(this.platformID)){
        this.loadService.getCountries()
      }
      else if (Globals.templates.length == 0 && isPlatformBrowser(this.platformID)){
        this.loadService.getTemplates()
      } 
      else if (Globals.cart == undefined && isPlatformBrowser(this.platformID)){
        if (await this.loadService.authenticated()){
          this.loadService.getCart()
        }
        else{
          this.loadedCart = true
          Globals.cart = []
          console.log("Cart Loaded! bad")
          this.cdr.detectChanges()
        }
      }
      else if (Globals.shippingInfo == undefined && isPlatformBrowser(this.platformID)){
        this.loadService.getShippingAddress()
      }
      else{
        this.loadedCart = true
        console.log(Globals.cart![0].product?.name)
        console.log("Cart Loaded!")
        this.cdr.detectChanges()
      }
    }
  }

  init() {

    const storeName = this.getStoreName()

    Globals.cart = undefined
    this.downloadAllStoreInfo(storeName)

  }

  isBrowser(){
    return isPlatformBrowser(this.platformID)
  }

  hasCart(){
    return (Globals.cart?.length != 0 ?? false)
  }

  downloadAllStoreInfo(storeName: string){
    this.loadService.myCallback = () => this.callback()
    this.loadService.getUser(storeName)
  }

  formatPrice(price: number){
    var priceAsString = new String((price * Globals.selectedCurrency!.rate).toFixed(2))
    let index = priceAsString.indexOf(".")

    switch (index){
    case priceAsString.length - 1:
        priceAsString += "00"
        break
    case priceAsString.length - 2:
        priceAsString += "0"
        break
    default:

        break
    }
    return this.numberWithCommas(this.getCurrencyForCountry(Globals.selectedCurrency!, Globals.selectedCurrency!.name != "US") + priceAsString)
  }

  numberWithCommas(x: string) {
    return x.replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  }

  getCurrencyForCountry(country: Country, shouldShowName: boolean){

    var returnItem = country.currency_symbol
    if (shouldShowName) returnItem = country.name + " " + returnItem

    return returnItem
  }

  addTags(title: string, imgUrl: string, description: string, url: string){
    this.metaService.updateTag({property: 'og:title', content: title  + " - " + "Home"});
    this.metaService.updateTag({property: 'og:image', content: imgUrl});
    this.metaService.updateTag({property: 'og:url', content: url})
    this.metaService.updateTag({property: 'og:description', content: description})
    this.titleService.setTitle(title)
    this.metaService.updateTag({property: 'description', content: description})

  }

  routeToShipping(){
    const storeName = this.getStoreName()

    if (Globals.shippingInfo?.email){
      this._router.navigateByUrl(storeName + '/review-order')
    }
    else{
      this._router.navigateByUrl(storeName + '/shipping-address')
    }
  }

  next(){

    this.routeToShipping()
  }

  getStoreName(){
    const routeParams = this.router.snapshot.paramMap;
    const storeID = routeParams.get('user') as string;

  // Find the product that correspond with the id provided in route.
    console.log(storeID)
    return storeID
  }

  constructor(
    @Inject(PLATFORM_ID) private platformID: Object,
    private cdr: ChangeDetectorRef,
    private router: ActivatedRoute,
    private titleService: Title, 
    private metaService: Meta,
    private loadService: LoadService,
    private _router: Router

  ) {}

  ngOnInit(): void {
    this.init()
  }

}
<router-outlet></router-outlet>

  <!-- Header-->
  <header *ngIf="isBrowser();" class="bg-dark py-6">
    <div class="cover" style="background-image: url(https://firebasestorage.googleapis.com/v0/b/clothingapp-ed125.appspot.com/o/Resources%2FSample-Walls%2FHome%2F1.jpg?alt=media);">
        <div class="d-flex align-items-center justify-content-center" style="height: 200px; font-weight: bold;font-size:40px">
            <p class = "text-center" style = "line-height:1.2em;text-shadow: 2px 2px #42424281;">
              SHOPPING BAG
             </p>
           </div>
      </div>
  </header>

  <section *ngIf="isBrowser();" [hidden]="stillLoadingProducts()" class="py-5">
    <div class="container pb-5 mb-2 mb-md-4">
        <div class="row">
          <!-- List of items-->
          <!-- <div class="d-flex justify-content-left align-items-center pb-4 pb-sm-5 mt-1 ms-2 ps-1">
            <a class="btn btn-outline-primary btn-sm ps-2" href="shop-grid-ls.html"><i class="ci-arrow-left me-2"></i>Continue shopping</a>
          </div> -->
          <section class="col-lg-8">
            <div *ngIf="hasCart() && loadedCart;" class="bg-white rounded-3 shadow-lg p-4 pt-3 mt-0">
                <h2 class="h6 mb-1 pb-1 ps-3 mt-2">My Shopping Bag</h2>
                <div class="py-2 px-xl-2">
                    <div *ngFor="let product of cart()" class="d-sm-flex justify-content-between align-items-center pb-3 pt-3">
                        <div class="d-block d-sm-flex align-items-center text-center text-sm-start"><a class="d-inline-block flex-shrink-0 mx-auto me-sm-4" href="shop-single-v1.html"><img style="border-radius: 2.5%;" src="{{product.product!.url}}" width="160" alt="Product"/></a>
                          <div class="pt-2">
                              <p><a href="/{{storeInfo().username}}/products/{{product.product!.productID}}" class="text-dark text-decoration-none" style="font-weight: bold;">{{product.product?.name ?? ""}}</a></p>
                            <div class="fs-sm"><span class="text-muted me-2">Size:</span>{{product.size ?? "M"}}</div>
                            <div class="fs-sm"><span class="text-muted me-2">Color:</span>{{getColor(product.product)}}</div>
                            <div class="fs-lg text-accent pt-2">{{formatPrice(product.product!.price / 100)}}</div>
                          </div>
                        </div>
                        <div class="pt-2 pt-sm-0 ps-sm-3 mx-auto mx-sm-0 text-center text-sm-start" style="max-width: 5.5rem;">
                          <label class="form-label" for="quantity1">Quantity</label>
                          <input #qtyChooser class="form-control" type="number" id="quantity1" min="1" value="{{product.quantity ?? 1}}" (change)="updateProductQty(product, qtyChooser.value)"/>
                          <button class="btn btn-link px-0 text-danger" type="button" (click)="updateProductQty(product, '0')"><i class="ci-close-circle me-0"></i><span class="fs-sm" style="font-size: 10px;">Remove</span></button>
                        </div>
                    </div>
                </div>
            </div>
            <div *ngIf="!(hasCart()) && loadedCart;" class="bg-white rounded-3 shadow-lg p-4 pt-3 mt-0">
                <h2 class="h6 ps-3 mt-2 text-center">Your Shopping Bag is Empty</h2>
                <div class="py-2 px-xl-2">
                    <div class="d-block align-items-center pt-2">
                        <div class="d-block align-items-center text-center text-sm-start"><a class="mx-auto me-sm-4" href="shop-single-v1.html"><img style="border-radius: 2.5%;" src="https://firebasestorage.googleapis.com/v0/b/clothingapp-ed125.appspot.com/o/Resources%2FSample-Walls%2FHome%2F1.jpg?alt=media" style="max-width: 100%; height: auto;" alt="Product"/></a>
                          <div class="pt-2">
                              <h2 class="h6 ps-3 mt-2 text-center"><a href="/{{storeInfo().username}}/products" class="text-dark text-decoration-underline" style="font-weight: bold;">Shop More Products</a></h2>
                          </div>
                        </div>
                    </div>
                </div>
            </div>
          </section>
          <!-- Sidebar-->
          <aside class="col-lg-4 pt-4 pt-lg-0 ps-xl-5">
            <div class="bg-white rounded-3 shadow-lg p-4">
              <div class="py-2 px-xl-2">
                <div [hidden]="stillLoadingProducts()" class="text-center mb-4 pb-3 border-bottom">
                  <h2 class="h6 mb-3 pb-1">Subtotal</h2>
                  <h3 class="fw-normal">{{formatPrice(total())}}</h3>
                </div>
                <div class="accordion" id="order-options">
                  <!-- <div class="accordion-item">
                    <h3 class="accordion-header"><a class="accordion-button" href="#promo-code" role="button" data-bs-toggle="collapse" aria-expanded="true" aria-controls="promo-code">Apply promo code</a></h3>
                    <div class="accordion-collapse collapse show" id="promo-code" data-bs-parent="#order-options">
                      <form class="accordion-body needs-validation" novalidate="novalidate">
                        <div class="mb-3">
                          <input (keyup)="onKey($event)"class="form-control" type="text" placeholder="Promo code" required="required"/>
                          <div class="invalid-feedback">Please provide promo code.</div>
                        </div>
                        <button class="btn btn-outline-primary d-block w-100" type="button" (click) = "calculateDiscount()">Apply promo code</button>
                      </form>
                    </div>
                  </div> -->
                  <div class="accordion-item">
                    <h3 class="accordion-header"><a class="accordion-button collapsed" href="#shipping-estimates" role="button" data-bs-toggle="collapse" aria-expanded="true" aria-controls="shipping-estimates">Shipping estimates</a></h3>
                    <div class="accordion-collapse collapse" id="shipping-estimates" data-bs-parent="#order-options">
                      <div class="accordion-body">
                        <form class="needs-validation" novalidate="novalidate">
                          <div class="mb-3">
                            <h2 class="h5 mb-3 pb-1">Shipping: {{shipping()}}</h2>
                            <select class="form-select" (change)="calculateShipping($event)" required="required">
                              <option value="">Choose your country</option>
                              <option value="United States">United States</option>
                              <option value="Canada">Canada</option>
                              <option value="United Kingdom">United Kingdom</option>
                            </select>

                            <div class="invalid-feedback">Please choose your country!</div>
                          </div>

                          <!-- <div class="mb-3">
                            <select class="form-select" required="required">
                              <option value="">Choose your city</option>
                              <option value="Bern">Bern</option>
                              <option value="Brussels">Brussels</option>
                              <option value="Canberra">Canberra</option>
                              <option value="Helsinki">Helsinki</option>
                              <option value="Mexico City">Mexico City</option>
                              <option value="Ottawa">Ottawa</option>
                              <option value="Washington D.C.">Washington D.C.</option>
                              <option value="Wellington">Wellington</option>
                            </select>
                            <div class="invalid-feedback">Please choose your city!</div>
                          </div> -->
                          <!-- <div class="mb-3">
                            <input class="form-control" type="text" placeholder="ZIP / Postal code" required="required"/>
                            <div class="invalid-feedback">Please provide a valid zip!</div>
                          </div> -->

                          <button class="btn btn-outline-primary d-block w-100" type="button" (click)="calculate()">Calculate shipping</button>
                        </form>
                      </div>
                    </div>
                  </div>
                </div><a *ngIf="hasCart() && loadedCart;" class="btn btn-primary btn-shadow d-block w-100 mt-4" type="button" (click) = "next()"><i class="ci-card fs-lg me-2"></i>Proceed to Checkout</a>
              </div>
            </div>
          </aside>
        </div>
      </div>
  </section>
