import { Component, OnInit, Inject, PLATFORM_ID, ChangeDetectorRef, AfterViewInit } from '@angular/core';
import { Globals } from '../globals';
import { Product } from '../models/product.model';
import { ProductInCart } from '../models/product-in-cart.model';
import { Dict, LoadService } from '../services/load.service';
import { isPlatformBrowser } from '@angular/common';
import { Country } from '../models/shipping-country.model';
import { ActivatedRoute, Router } from '@angular/router';
import { Title, Meta } from '@angular/platform-browser';
import { NgxSpinnerService } from "ngx-spinner";
import { AppComponent } from '../app.component';
import { StripeService, STRIPE_PUBLISHABLE_KEY, WindowRef, StripePaymentRequestButtonComponent } from 'ngx-stripe';
import { Stripe, PaymentRequestPaymentMethodEvent, StripeElementsOptions, StripePaymentRequestButtonElementOptions } from '@stripe/stripe-js';
import { switchMap } from 'rxjs/operators';
import { of } from 'rxjs';

@Component({
  selector: 'app-checkout',
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.css']
})
export class CheckoutComponent implements OnInit, AfterViewInit {

  storeInfo(){return Globals.storeInfo}

  availableCurrencies(){return Globals.availableCurrencies}

  selectedCurrency(){return Globals.selectedCurrency}

  templates(){return Globals.availableTemplates}

  availableTemplates(){return Globals.availableTemplates}


  selectedProduct(){return Globals.selectedProduct}

  selectedTemplate(){return Globals.selectedTemplate}

  shippingAddress(){return Globals.shippingInfo}

  billingAddress(){return Globals.billingInfo}

  tax(){return Globals.shippingTax}

  selectedCountry!: string
  selectedCode!: string

  useCard = false

  err = ""

  cartLength(){
    if (this.rootComponent.cart?.length == 0){

      return ""
    }
    return this.rootComponent.cart?.length
  }

  cart(){
    return this.rootComponent.cart ?? []
  }

  routeToCart(){
    const storeName = this.getStoreName()
    this._router.navigateByUrl(storeName + '/' + "cart")
  }

  productTax(){
    let tax = this.productTaxNum() ?? 0
    if (tax > 0){
      return this.numberWithCommas(this.formatPrice((tax ?? 0)))
    }
    return "N/A"
  }



  productTaxNum(){
    return (this.total() + this.shippingNum()) * (this.tax() ?? 0)
  }

  orderTotal(){
    return this.numberWithCommas(this.formatPrice(this.total() + this.shippingNum() + this.productTaxNum()))
  }

  getColor(product?: Product){

    let colors = Globals.templates.find(template => template.productCode == product?.productType)?.colors
    return colors?.find(color => color.code == product?.templateColor)?.display ?? "White"
  }

  stillLoadingProducts(){
    return this.rootComponent.cart?.filter(product => product.product?.price == undefined)?.length != 0
  }

  stillLoading(){
    return (this.rootComponent.cart?.filter(product => product.product?.price == undefined)?.length != 0) 
    || 
    Globals.availableCurrencies?.filter(currency => currency.shipping_rate_usd == undefined)?.length != 0
    ||
    Globals.shippingTax == undefined

  }

  calculateShipping($event: any){

    let country = $event.target.options[$event.target.options.selectedIndex].text;

    this.selectedCountry = country
  }

  onKey(event: any) {
    const inputValue = event.target.value;
    this.selectedCode = inputValue
  }

  calculateDiscount(){


    // this.selectedCountry = country
  }

  async updateProductQty(product: ProductInCart, newQty: string){

    product.quantity = Number(newQty)

    let mappedData: Array<Dict<any>> = []
    let dataToRemove: Array<ProductInCart> = []

    this.rootComponent.cart?.forEach((cartProduct, index) => {
      if ((cartProduct.quantity ?? 0) > 0){
        let data = {
          "UID" : cartProduct.product?.uid,
          "Size" : cartProduct.size,
          "Qty" : cartProduct.quantity,
          "Timestamp" : cartProduct.timestamp,
          "Post_ID" : cartProduct.product?.productID
        }
        mappedData.push(data)
      }
      else{
        dataToRemove.push(cartProduct)
      }
    })

    this.rootComponent.cart = this.rootComponent.cart?.filter(cartProduct => !(dataToRemove.includes(cartProduct)))

    await this.loadService.changeCart(mappedData)
    if (this.rootComponent.cart?.length == 0){
      this.routeToCart()
      return
    }
    this.cdr.detectChanges()
    // this.selectedCountry = country
  }

  calculate(){
    this.cdr.detectChanges()
  }

  shipping(){
    let shipping = (Globals.availableCurrencies.find(country => country.name_full == this.selectedCountry)?.shipping_rate_usd ?? 0) / 100
    if (shipping > 0){
      return this.numberWithCommas(this.formatPrice(shipping))
    }
    return "FREE"
  }

  shippingNum(){
    return (Globals.availableCurrencies.find(country => country.name_full == this.selectedCountry)?.shipping_rate_usd ?? 0) / 100
  }


  total(){
    var total = 0

    this.rootComponent.cart?.forEach(product => {
      total += (product.product?.price ?? 0) * (product.quantity ?? 1)
    })
    return total / 100
  }

  format(d: Date) {
    return Globals.months[d.getMonth()].substring(0,3) + " " + d.getDate() + " " + d.getFullYear()
  }

  hasCart(){
    return this.rootComponent.cart?.length != 0 ?? false
  }



  async callback(){
    if (await this.loadService.authenticated()){
      if (Globals.storeInfo.username){
        this.cardOptions.style!.paymentRequestButton.theme = this.theme()

        this.loadingName = this.storeInfo()?.fullName ?? ''
        this.showSpinner()
        this.rootComponent.setFavIcon(Globals.storeInfo.profileLink!.toString())

        this.rootComponent.setOptions()
        this.addTags(Globals.storeInfo.fullName ?? "Thred", (Globals.storeInfo.profileLink ?? new URL("https://shopmythred.com")).toString(), Globals.storeInfo.bio ?? "Check out my Thred Store!", "shopmythred.com/" + Globals.storeInfo.username)

        if (Globals.userInfo == undefined && isPlatformBrowser(this.platformID)){
          this.loadService.getCustomer()
        }
        else if (this.rootComponent.cart?.length == 0 && isPlatformBrowser(this.platformID)){
          this.routeToCart()
          return
        }
        else if (Globals.shippingInfo == undefined && isPlatformBrowser(this.platformID)){
          this.loadService.getShippingAddress()
        }
        else if ((Globals.shippingInfo?.email == "" || Globals.shippingInfo?.email == undefined) && isPlatformBrowser(this.platformID)){
          this.routeToCart()
          return
        }
        else if (!this.useOtherPaymentMethod && Globals.billingInfo == undefined && isPlatformBrowser(this.platformID)){
          this.loadService.getBillingInfo()
        }
        else if (!this.useOtherPaymentMethod && (Globals.billingInfo?.number == "" || Globals.billingInfo?.number == undefined) && isPlatformBrowser(this.platformID)){
            this.routeToBilling()
            return
        }
        else if (Globals.availableCurrencies.length == 0 && isPlatformBrowser(this.platformID)){
          this.loadService.getCountries()
        }
        else if (Globals.templates.length == 0 && isPlatformBrowser(this.platformID)){
          this.loadService.getTemplates()
        } 
        else if (Globals.billingInfo?.admin_area == undefined || Globals.billingInfo?.admin_area == "" && isPlatformBrowser(this.platformID)){
          this.loadService.getBillingAddress()
        }
        else if (Globals.shippingTax == undefined && Globals.billingInfo?.admin_area && Globals.billingInfo?.admin_area != "" && isPlatformBrowser(this.platformID)){
          this.loadService.getTax(this.billingAddress()!)
        }
        else{

          console.log(Globals.billingInfo.country)
          let same = Globals.availableCurrencies?.filter(currency => { return currency.name_full == Globals.billingInfo?.country})


          if (same && same.length != 0){

            const amount = Math.round((((this.total() + this.shippingNum() + this.productTaxNum()) * same[0].rate ?? 1)* 100))
            console.log(same[0].currency_name)
            console.log(amount)

            this.paymentRequestOptions = {
              country: same[0].name,
              currency: same[0].currency_name.toLowerCase(),
              total: {
                label: Globals.storeInfo.fullName ?? "Thred Apps Inc.",
                amount: amount,
              },
              requestPayerName: true,
              requestPayerEmail: true,
            };

            console.log(this.paymentRequestOptions)
          }
          else{
            const amount = Math.round(((this.total() + this.shippingNum() + this.productTaxNum()) * 100))
            console.log(same[0].currency_name)
            console.log(amount)

            this.paymentRequestOptions = {
              country: 'US',
              currency: 'usd',
              total: {
                label: Globals.storeInfo.fullName ?? "Thred Apps Inc.",
                amount: amount,
              },
              requestPayerName: true,
              requestPayerEmail: true,
            };
          }


          this.cdr.detectChanges()
        }
      }
      else{
          this.routeTo404()
      }
    }
    else{
      this.routeToCart()
    }
  }

  theme(){
    if (this.storeInfo().colorStyle.back_code == 'light'){
      return 'dark'
    }
    return 'light'
  }

  routeTo404(){
    const storeName = this.getStoreName()
    this._router.navigateByUrl(storeName + '/' + "not-found")
  }

  loadingName = ''
  selectedIndicator(){

    let co = Globals.storeInfo?.loading?.color
    let bco = Globals.storeInfo?.loading?.bg_color
    let name = Globals.storeInfo?.loading?.name

    let color = "rgba(" + co[0] + "," + co[1] + "," + co[2] + "," + co[3] + ")"

    let bg_color = "rgba(" + bco[0] + "," + bco[1] + "," + bco[2] + "," + bco[3] + ")"

    var indicator: Dict<string> = {
      "name": name,
      "color": color,
      "bg_color": bg_color
    }
    return indicator
  }

  selectedTheme(){

    let co = Globals.storeInfo?.colorStyle?.btn_color
    let bco = Globals.storeInfo?.colorStyle?.bg_color
    let name = Globals.storeInfo?.colorStyle?.name

    let color = "rgba(" + co[0] + "," + co[1] + "," + co[2] + "," + co[3] + ")"

    let bg_color = "rgba(" + bco[0] + "," + bco[1] + "," + bco[2] + "," + bco[3] + ")"

    var theme: Dict<string> = {
      "name": name,
      "color": color,
      "bg_color": bg_color
    }
    return theme
  }

  isSpinning = false

    showSpinner(){
      if (!this.isSpinning){
        this.isSpinning = true
        if (isPlatformBrowser(this.platformID)){
          this.spinner.show()
        };

        setTimeout(() => {
            this.spinner.hide()
        }, 1500);
      }
    }

    hideSpinner(){
      if (this.isSpinning){
        this.isSpinning = false
        // this.spinner.hide()
      }
    }

    routeToProduct(productID: string){
      if (Globals.storeInfo?.username)
      this._router.navigateByUrl(Globals.storeInfo?.username + "/" + "products/" + productID)
    }

    routeToShipping(){
      if (Globals.storeInfo?.username)
      this._router.navigateByUrl(Globals.storeInfo?.username + '/shipping-address')
    }

    routeToBilling(){
      if (Globals.storeInfo?.username){
        this._router.navigateByUrl(Globals.storeInfo?.username + '/billing-info')
      }
    }

    routeToShop(){
      if (Globals.storeInfo?.username){
        this._router.navigateByUrl(Globals.storeInfo?.username + "/" + "products")
      }
    }

  placeOrder(event: any){
    /** spinner starts on init */
    this.loadingName = 'Placing Order...'

    if (isPlatformBrowser(this.platformID)){
          this.spinner.show()
   };

    event.target.disabled = true;

    let storeID = this.storeInfo().uid!
    this.loadService.createPayment(storeID, true, (order_id: string, client_secret: string) => {
      this.finishedOrder(order_id, client_secret)
    })
  }

  finishedOrder(order_id: string, _client_secret: string){
    this.loadingName = ''
    if (isPlatformBrowser(this.platformID)){
          this.spinner.hide()
   };
    const storeName = this.getStoreName()
    this._router.navigateByUrl(storeName + '/' + "orders/" + order_id)
  }

  init() {

    const storeName = this.getStoreName()

    Globals.shippingInfo = undefined
    Globals.billingInfo = undefined
    Globals.shippingTax = undefined

    this.downloadAllStoreInfo(storeName)

  }


  onPaymentMethod(ev: PaymentRequestPaymentMethodEvent) {



    let storeID = this.storeInfo().uid!
    this.loadService.createPayment(storeID, false, (order_id: string, client_secret: string) => {
      return this.stripeService.confirmCardPayment(
        client_secret,
        { payment_method: ev.paymentMethod.id },
        { handleActions: false }
      ).subscribe((confirmResult) => {
        if (confirmResult.error) {
          // Report to the browser that the payment failed, 
          // prompting it to re-show the payment interface, 
          // or show an error message and close the payment.
          ev.complete('fail');
          console.log(confirmResult.error)
          return of({
            error: new Error('Error Confirming the payment'),
          });
        } else {
          // Report to the browser that the confirmation was 
          // successful, prompting it to close the browser 
          // payment method collection interface.
          console.log('success')

          ev.complete('success');
          // Let Stripe.js handle the rest of the payment flow.
          return this.finishedOrder(order_id, client_secret)
        }
      })
    })
  }

  getShippingAddressSecond(){
    var address = this.shippingAddress()?.street
    var unit = this.shippingAddress()?.unit
    if (unit) address += ". " + unit
    return address
  }

  getShippingAddressThird(){
    return this.shippingAddress()?.city + ", " + this.shippingAddress()?.admin_area + ". " + this.shippingAddress()?.postal
  }

  itemCount(){
    var count = 0
    this.rootComponent.cart?.forEach(product => {
      count += (product.quantity ?? 0)
    });
    return count
  }



  getShippingAddressFirst(){
    var name = this.shippingAddress()?.name
    var company = this.shippingAddress()?.company
    if (company) name += " - " + company
    return name
  }

  getBillingAddressFirst(){
    var name = this.billingAddress()?.brand + " ending in " + this.billingAddress()?.number

    if (this.useOtherPaymentMethod){
      name = ""
    }
    return name
  }

  isBrowser(){
    return isPlatformBrowser(this.platformID)
  }

  downloadAllStoreInfo(storeName: string){
    this.loadService.myCallback = () => this.callback()
    this.loadService.getUser(storeName)
  }

  formatPrice(price: number){
    var priceAsString = new String((price * Globals.selectedCurrency!.rate).toFixed(2))
    let index = priceAsString.indexOf(".")

    switch (index){
    case priceAsString.length - 1:
        priceAsString += "00"
        break
    case priceAsString.length - 2:
        priceAsString += "0"
        break
    default:

        break
    }
    return this.numberWithCommas(this.getCurrencyForCountry(Globals.selectedCurrency!, Globals.selectedCurrency!.name != "US") + priceAsString)
  }

  numberWithCommas(x: string) {
    return x.replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  }

  getCurrencyForCountry(country: Country, shouldShowName: boolean){

    var returnItem = country.currency_symbol
    if (shouldShowName) returnItem = country.name + " " + returnItem

    return returnItem
  }

  addTags(title: string, imgUrl: string, description: string, url: string){
    this.metaService.updateTag({property: 'og:title', content: title  + " - " + "Home"});
    this.metaService.updateTag({property: 'og:image', content: imgUrl});
    this.metaService.updateTag({property: 'og:url', content: url})
    this.metaService.updateTag({property: 'og:description', content: description})
    this.titleService.setTitle(title)
    this.metaService.updateTag({property: 'description', content: description})

  }

  useOtherPaymentMethod = false

  getStoreName(){
    const routeParams = this.router.snapshot.paramMap;
    const storeID = routeParams.get('user') as string;

  // Find the product that correspond with the id provided in route.
    return storeID
  }

  paymentRequestOptions = {
    country: 'US', 
    currency: 'usd', 
    total: {
      label: "Thred Apps Inc.",
      amount: 0,
    },
    requestPayerName: false,
    requestPayerEmail: false,
  }

  cardOptions: StripePaymentRequestButtonElementOptions = {
    style:{
      paymentRequestButton:{
        theme:'dark',
      }
    },
  };


  constructor(
    @Inject(PLATFORM_ID) private platformID: Object,
    @Inject(STRIPE_PUBLISHABLE_KEY) private stripeKey: Object,

    private cdr: ChangeDetectorRef,
    private router: ActivatedRoute,
    private _router: Router,
    private titleService: Title, 
    private metaService: Meta,
    private loadService: LoadService,
    private spinner: NgxSpinnerService,
    private rootComponent: AppComponent,
    private stripeService: StripeService,

  ) {
    this.useOtherPaymentMethod = Globals.useOtherPaymentMethod ?? false
  }
  ngAfterViewInit(): void {

  }

  stripe?: Stripe;
  prButton: any;
  elements: any;



  ngOnInit(): void {
    this.init()







  }

}
<router-outlet></router-outlet>


<ngx-spinner
*ngIf="isBrowser()" 
bdColor = '{{selectedIndicator().bg_color}}'
size = "large"
color = '{{selectedIndicator().color}}'
type = {{selectedIndicator().name}} 
[fullScreen] = "true"
style.color = "{{selectedIndicator()!.color}}"
>
<h3 class="text-center mt-5" [style.font-family]="storeInfo().fontName!" class="text-center"> {{loadingName}}
</h3>
</ngx-spinner>
  <!-- Header-->
  <header *ngIf="isBrowser() && hasCart() && storeInfo().username != undefined" [hidden]="stillLoadingProducts()" class="bg-{{storeInfo().colorStyle!.back_code}} py-6">
    <div class="cover" style="background-image: url(https://firebasestorage.googleapis.com/v0/b/clothingapp-ed125.appspot.com/o/Resources%2FSample-Walls%2FHome%2F1.jpg?alt=media);" [style.background-image]='"url(" + storeInfo()!.themeLink!.toString() + ")"'>
        <div class="d-flex align-items-center justify-content-center" style="height: 200px; font-weight: bold;font-size:35px">
          <div>
            <p [style.font-family]="storeInfo().fontName!" class = "text-center" style = "line-height:1.2em;text-shadow: 2px 2px 4px #42424281;">
              REVIEW ORDER
             </p>
             <p [style.font-family]="storeInfo().fontName!" class = "text-center" style = "line-height:1.2em; font-size:25px; text-shadow: 2px 2px 4px #42424281;">
              SECURE CHECKOUT
              <mat-icon [inline]='true'>
                lock
              </mat-icon>
             </p>
          </div>
           </div>
      </div>
  </header>

  <section *ngIf="isBrowser() && hasCart() && storeInfo().username != undefined" [hidden]="stillLoadingProducts()" class="py-5" style.background-color="{{selectedTheme()!.bg_color}}">
    <div class="container pb-5 mb-2 mb-md-4">
        <div class="row">
          <!-- List of items-->
          <!-- <div class="d-flex justify-content-left align-items-center pb-4 pb-sm-5 mt-1 ms-2 ps-1">
            <a class="btn btn-outline-primary btn-sm ps-2" href="shop-grid-ls.html"><i class="ci-arrow-left me-2"></i>Continue shopping</a>
          </div> -->

          <section class="col-lg-8">

            <div class="bg-{{storeInfo().colorStyle!.back_code}} rounded-3 shadow-lg p-2 pb-1">
                <div class="py-0 px-xl-2">
                  <div [hidden]="stillLoadingProducts()" class="text-accent">
                    <h2 [style.font-family]="storeInfo().fontName!" style.color="{{selectedTheme()!.color}}" class="h6 mb-3 pb-1 ps-4 mt-2">1. Shipping Address</h2>
                    <h3 [style.font-family]="storeInfo().fontName!" style.color="{{selectedTheme()!.color}}" class="fw-normal ps-4" style="font-style: italic">{{getShippingAddressFirst()}}</h3>
                    <h3 [style.font-family]="storeInfo().fontName!" style.color="{{selectedTheme()!.color}}" class="fw-normal ps-4" style="font-style: italic">{{getShippingAddressSecond()}}</h3>
                    <h3 [style.font-family]="storeInfo().fontName!" style.color="{{selectedTheme()!.color}}" class="fw-normal ps-4" style="font-style: italic">{{getShippingAddressThird()}}</h3>
                    <p><a [style.font-family]="storeInfo().fontName!" style.color="{{selectedTheme()!.color}}" role="button" (click)="routeToShipping()" href="/{{storeInfo().username}}/shipping-address" class="text-primary text-decoration-none ps-4" style="font-weight: bold;">Change</a></p>
                  </div>
                </div>
            </div>

            <div class="bg-{{storeInfo().colorStyle!.back_code}} rounded-3 shadow-lg p-2 pt-3 pb-1 mt-3">
                <div class="py-0 px-xl-2">
                  <div [hidden]="stillLoadingProducts()" class="text-accent">
                    <h2 [style.font-family]="storeInfo().fontName!" style.color="{{selectedTheme()!.color}}" class="h6 mb-3 pb-1 ps-4 mt-2">2. Payment Method </h2>
                    <h3 [style.font-family]="storeInfo().fontName!" style.color="{{selectedTheme()!.color}}" class="fw-normal ps-4" style="font-style: italic">{{getBillingAddressFirst()}}</h3>
                    <p><a [style.font-family]="storeInfo().fontName!" style.color="{{selectedTheme()!.color}}" role="button" (click)="routeToBilling()" class="text-primary text-decoration-none ps-4" style="font-weight: bold;">Change</a></p>
                    </div>
                </div>
            </div>
            <!-- Item-->
            <div class="bg-{{storeInfo().colorStyle!.back_code}} rounded-3 shadow-lg p-4 pt-3 mt-3">
                <h2 [style.font-family]="storeInfo().fontName!" style.color="{{selectedTheme()!.color}}" class="h6 mb-1 pb-1 ps-2 mt-2">3. Review Order</h2>
                <div class="py-2 px-xl-2">
                    <div *ngFor="let product of cart(); index as i" class="d-sm-flex justify-content-between align-items-center pb-3 pt-3">
                        <div class="d-block d-sm-flex align-items-center text-center text-sm-start"><a class="d-inline-block flex-shrink-0 mx-auto me-sm-4" role="button" (click)="routeToProduct(product.product!.productID)"><img style="border-radius: 2.5%;" src="{{product.product!.url}}" onerror="this.src='https://firebasestorage.googleapis.com/v0/b/clothingapp-ed125.appspot.com/o/Resources%2Flink_empty.jpeg?alt=media';" width="160" alt="Product"/></a>
                          <div class="pt-2">
                              <p><a [style.font-family]="storeInfo().fontName!" role="button" (click)="routeToProduct(product.product!.productID)" class="text-{{storeInfo().colorStyle!.text_code}} text-decoration-none" style="font-weight: bold;" style.color="{{selectedTheme()!.color}}">{{product.product?.name ?? ""}}</a></p>
                            <div [style.font-family]="storeInfo().fontName!" style.color="{{selectedTheme()!.color}}" class="fs-sm"><span class="text-muted me-2">Size:</span>{{product.size ?? "M"}}</div>
                            <div [style.font-family]="storeInfo().fontName!" style.color="{{selectedTheme()!.color}}" class="fs-sm"><span class="text-muted me-2">Color:</span>{{getColor(product.product)}}</div>
                            <div [style.font-family]="storeInfo().fontName!" style.color="{{selectedTheme()!.color}}" class="fs-lg text-accent pt-2">{{formatPrice(product.product!.price / 100)}}</div>
                          </div>
                        </div>
                        <div class="pt-2 pt-sm-0 ps-sm-3 mx-auto mx-sm-0 text-center text-sm-start" style="max-width: 5.5rem;">
                          <label [style.font-family]="storeInfo().fontName!" class="form-label" for="quantity_i" style.color="{{selectedTheme()!.color}}">Quantity</label>
                          <input [style.font-family]="storeInfo().fontName!" #qtyChooser class="form-control" type="number" id="quantity_i" min="1" value="{{product.quantity ?? 1}}" (change)="updateProductQty(product, qtyChooser.value)" style.background-color="{{selectedTheme()!.bg_color}}" style.color="{{selectedTheme()!.color}}"/>
                          <button [style.font-family]="storeInfo().fontName!" class="btn btn-link px-0 text-danger" type="button" (click)="updateProductQty(product, '0')"><i class="ci-close-circle me-0"></i><span class="fs-sm" style="font-size: 10px;">Remove</span></button>
                        </div>
                      </div>
                </div>
            </div>

          </section>
          <!-- Sidebar-->
          <aside class="col-lg-4 pt-4 pt-lg-0 ps-xl-5">
            <div [hidden]="stillLoading()" class="bg-{{storeInfo().colorStyle!.back_code}} rounded-3 shadow-lg p-4 pb-0">
              <div class="py-2 px-xl-2">
                <p [hidden]="err == ''" [style.font-family]="storeInfo().fontName!" class = "text-center mt-1 mb-3" style = "line-height:1.0em; font-size: 12px; height: 15px; font-weight: normal; color: red;">
                  {{err}}
                 </p>
                <div class="text-center mt-2"><a *ngIf="!useOtherPaymentMethod" [style.font-family]="storeInfo().fontName!" class="btn mb-4 d-flex flex-wrap" style="font-weight:bold; font-size:20px; justify-content: center;" (click)="placeOrder($event)" style.background-color="{{selectedTheme()!.color}}" style.color="{{selectedTheme()!.bg_color}}"  [innerHTML]="'Place Order'"></a></div>

                <ngx-stripe-payment-request-button
                *ngIf="isBrowser() && tax() != undefined && billingAddress()?.admin_area != undefined && billingAddress()?.admin_area != ''"
                [paymentOptions]="paymentRequestOptions"
                [options]="cardOptions"
                (paymentMethod)="onPaymentMethod($event)"
                class="mb-4"
                ></ngx-stripe-payment-request-button>

                <div class="text-center mt-4 pt-3 border-top border-bottom">
                <div class="d-flex justify-content-between">
                    <h3 style.color="{{selectedTheme()!.color}}" class="fw-bold" style="font-size: 9;">Items ({{itemCount()}}):</h3>
                    <h3 style.color="{{selectedTheme()!.color}}" class="fw-normal" style="font-size: 9;">{{formatPrice(total())}}</h3>
                </div>
                <div class="d-flex justify-content-between">
                    <h3 style.color="{{selectedTheme()!.color}}" class="fw-bold" style="font-size: 9;">Shipping & Handling:</h3>
                    <h3 style.color="{{selectedTheme()!.color}}" class="fw-normal" style="font-size: 9;">{{shipping()}}</h3>
                </div>
                <div class="d-flex justify-content-between">
                    <h3 style.color="{{selectedTheme()!.color}}" class="fw-bold" style="font-size: 9;">Sales Tax:</h3>
                    <h3 style.color="{{selectedTheme()!.color}}" class="fw-normal" style="font-size: 9;">{{productTax()}}</h3>
                </div>
                </div>
                <div class="d-flex justify-content-between pt-2 mt-2">
                    <h3 style.color="{{selectedTheme()!.color}}" class="fw-bold" style="font-size: 9;">Order Total:</h3>
                    <h3 style.color="{{selectedTheme()!.color}}" class="fw-normal" style="font-size: 9;">{{orderTotal()}}</h3>
                </div>
              </div>
            </div>
          </aside>
        </div>
      </div>
  </section>
