              </div>
          </div>

               </div>
            </div>
            <div *ngIf="!(hasCart()) && loadedCart;" class="bg-white rounded-3 shadow-lg p-4 pt-3 mt-0">
                <h2 class="h6 ps-3 mt-2 text-center" style="font-weight: bolder;">Your Order History is Empty</h2>
                <div class="py-2 px-xl-2">
                    <div class="d-block align-items-center pt-2">
                        <div class="d-block align-items-center text-center text-sm-start"><a class="mx-auto me-sm-4" href="shop-single-v1.html"><img style="border-radius: 2.5%;" src="https://firebasestorage.googleapis.com/v0/b/clothingapp-ed125.appspot.com/o/Resources%2FSample-Walls%2FHome%2F1.jpg?alt=media" style="max-width: 100%; height: auto;" alt="Product"/></a>
                          <div class="pt-2">
                              <h2 class="h6 ps-3 mt-2 text-center"><a href="/{{storeInfo().username}}/products" class="text-dark text-decoration-underline" style="font-weight: bold;">Shop More Products</a></h2>
                          </div>
                        </div>
                    </div>
                </div>
            </div>
          </section>
          <!-- Sidebar-->

        </div>
      </div>
  </section>
