import { Component, OnInit, ViewChild } from '@angular/core';
import { DomSanitizer } from '@angular/platform-browser';
import { Template } from '../models/template.model';
import { DragScrollComponent } from 'ngx-drag-scroll';



@Component({
  selector: 'app-design',
  templateUrl: './design.component.html',
  styleUrls: ['./design.component.css']
})
export class DesignComponent implements OnInit {

  myInnerHeight = 0
  templates?: Array<Template>
  sanitizer?: DomSanitizer

  @ViewChild('carousel', {read: DragScrollComponent}) ds?: DragScrollComponent;

  moveLeft() {
    this.ds?.moveLeft();
  }

  moveRight() {
    this.ds?.moveRight();
  }

  moveTo(index: number) {
    this.ds?.moveTo(index);
  }

  constructor(
    sanitizer: DomSanitizer,
  ) { 
    this.sanitizer = sanitizer
  }

  ngOnInit(): void {
    let height = window.innerHeight
    let width = window.innerWidth

    if (width < height){
      this.myInnerHeight = width * 0.95
    }
    else{
      this.myInnerHeight = height * 0.65
    }

    this.ds!.xWheelEnabled = true

    this.loadTemplate()
  }

  async loadTemplate() {

    // var unsafeImageUrl = this.sanitizer.

    // this.base64Data = this.sanitizer.bypassSecurityTrustUrl(unsafeImageUrl);
  }

}
