import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-design',
  templateUrl: './design.component.html',
  styleUrls: ['./design.component.css']
})
export class DesignComponent implements OnInit {

  myInnerHeight = 0
  base64Data: any = ''
  blob: Blob = new Blob()

  constructor() { }

  ngOnInit(): void {
    this.myInnerHeight = window.innerHeight * (0.85)
    this.loadTemplate()
  }

  async loadTemplate() {
    var buffer = await this.blob.arrayBuffer();
    this.base64Data = buffer
  }
}
