import { Component, OnInit, Inject, PLATFORM_ID, ChangeDetectorRef, ViewChild } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Title, Meta } from '@angular/platform-browser';
import { LoadService, Dict } from '../services/load.service';
import { AppComponent } from '../app.component';
import { Globals } from '../globals';
import { isPlatformBrowser } from '@angular/common';
import { trigger, state, style, transition, animate } from '@angular/animations';
import { Validators, FormBuilder, FormGroup } from '@angular/forms';
import { ImageCroppedEvent, LoadedImage } from 'ngx-image-cropper';
import { NgbModal, NgbModalOptions } from '@ng-bootstrap/ng-bootstrap';
import { CropperComponent } from '../cropper/cropper.component';
import { NgxSpinnerService } from "ngx-spinner";
import { Country } from '../models/shipping-country.model';
import { Product } from '../models/product.model';
import { DesignComponent } from '../design/design.component';
import { ColorHelper, XAxisTicksComponent } from '@swimlane/ngx-charts';
import { ProductInCart } from '../models/product-in-cart.model';
import { SocialFormComponent } from '../social-form/social-form.component';

// import {
//   ChartComponent,
//   ApexAxisChartSeries,
//   ApexChart,
//   ApexXAxis,
//   ApexTitleSubtitle,
//   ApexResponsive
