  products(){return Globals.products}

  selectedProduct(){return Globals.selectedProduct}

  selectedTemplate(){return Globals.selectedTemplate}

  selectedCountry!: string
  selectedCode!: string

  cartLength(){
    if (Globals.cart?.length == 0){

      return ""
    }
    return Globals.cart?.length
  }

  cart(){
    return Globals.cart
  }

  shippingI(){
    return Globals.shippingInfo
  }

