  registerAccount(type: string, credentials?: Dict<string>){

    if (type == "Guest"){
      this.auth.signInAnonymously().then(() => {
        // Signed in..

        if (this.myCallback)
          this.myCallback()

        }).catch((error) => {
          var errorCode = error.code;
          var errorMessage = error.message;

          if (this.errCallback)
          this.errCallback(errorMessage)
        });
    }
    else if(type == "Email_UP"){

      let username = credentials?.username
      let password = credentials?.password!
      let email = credentials?.email!

      this.auth.createUserWithEmailAndPassword(email, password).then(async (result) => {
        // Signed in..
        await result.user?.sendEmailVerification()
        if (result.user && username){
          await this.setUsername(result.user?.uid, username, true)
          if (this.myCallback)
          this.myCallback()
        }
        else{
          await this.auth.signOut()
          if (this.myCallback)
          this.myCallback()
        }
        }).catch((error) => {
          var errorCode = error.code;
          var errorMessage = error.message;

          if (this.errCallback)
          this.errCallback(errorMessage)

          console.log(errorCode)

      });  
    }
    else if (type == "Apple"){
      var appleProvider = new firebase.auth.OAuthProvider('apple.com');
      appleProvider.addScope('email');
      appleProvider.addScope('name');

      this.auth.signInWithPopup(appleProvider).then(async (result) => {
        // Signed in..

        if ((result.additionalUserInfo?.isNewUser ?? false) && result.user?.uid){
          await this.setUsername(result.user?.uid, result?.additionalUserInfo?.username ?? 'user_' + uuid.toString(), (!(this.isUndefined(result?.additionalUserInfo?.username))))
        }
        if (this.myCallback)
          this.myCallback()

        }).catch((error) => {
          var errorCode = error.code;
          var errorMessage = error.message;

          if (this.errCallback)
          this.errCallback(errorMessage)
        });
    }
    else if (type == "Google"){
      var googleProvider = new firebase.auth.GoogleAuthProvider();
      this.auth.signInWithPopup(googleProvider).then(async (result) => {
        // Signed in..
        if ((result.additionalUserInfo?.isNewUser ?? false) && result.user?.uid){
          await this.setUsername(result.user?.uid, result?.additionalUserInfo?.username ?? 'user_' + uuid.toString(), (!(this.isUndefined(result?.additionalUserInfo?.username))))
        }
        if (this.myCallback)
          this.myCallback()

        }).catch((error) => {
          var errorCode = error.code;
          var errorMessage = error.message;

          if (this.errCallback)
          this.errCallback(errorMessage)
        });
    }
  }


